import React from 'react';
import './Loader.css'; 
import spinnerImage from 'image1.jpg'; 

const Loader = () => {
  return (
    <div className="loader-container">
      <img src={spinnerImage} alt="Loading..." className="spinner-image" />
      <p>Loading...</p>
    </div>
  );
};

export default Loader;
